  1→import { useCallback, useEffect, useMemo, useRef, useState } from "react";
  2→import type { ChatStatus } from "ai";
  3→import { PromptInputProvider } from "@ai-elements";
  4→import { toast } from "sonner";
  5→import { PanelLeftOpen, PanelLeftClose } from "lucide-react";
  6→import { cn } from "./lib/utils";
  7→import { ResizablePanel, ResizablePanelGroup } from "./components/ui/resizable";
  8→import { ChatWorkspaceContainer } from "./features/chat/chat-workspace-container";
  9→import { SessionsSidebar } from "./features/sessions/sessions";
 10→import { CreateSessionDialog } from "./features/sessions/create-session-dialog";
 11→import { Toaster } from "./components/ui/sonner";
 12→import { formatRelativeTime } from "./hooks/utils";
 13→import { useSessions } from "./hooks/useSessions";
 14→import { useTheme } from "./hooks/use-theme";
 15→import { ThemeToggle } from "./components/ui/theme-toggle";
 16→import type { SessionStatus } from "./lib/api/models";
 17→import type { PanelSize, PanelImperativeHandle } from "react-resizable-panels";
 18→import { consumeAuthTokenFromUrl, setAuthToken } from "./lib/auth";
 19→
 20→/**
 21→ * Get session ID from URL search params
 22→ */
 23→function getSessionIdFromUrl(): string | null {
 24→  const params = new URLSearchParams(window.location.search);
 25→  return params.get("session");
 26→}
 27→
 28→/**
 29→ * Update URL with session ID without triggering page reload
 30→ */
 31→function updateUrlWithSession(sessionId: string | null): void {
 32→  const url = new URL(window.location.href);
 33→  if (sessionId) {
 34→    url.searchParams.set("session", sessionId);
 35→  } else {
 36→    url.searchParams.delete("session");
 37→  }
 38→  window.history.replaceState({}, "", url.toString());
 39→}
 40→
 41→const SIDEBAR_COLLAPSED_SIZE = 48;
 42→const SIDEBAR_MIN_SIZE = 200;
 43→const SIDEBAR_DEFAULT_SIZE = 260;
 44→const SIDEBAR_ANIMATION_MS = 250;
 45→
 46→function App() {
 47→  // Initialize theme on app startup
 48→  useTheme();
 49→
 50→  const sidebarElementRef = useRef<HTMLDivElement | null>(null);
 51→  const sidebarPanelRef = useRef<PanelImperativeHandle | null>(null);
 52→  const sessionsHook = useSessions();
 53→  const [isMobileSidebarOpen, setIsMobileSidebarOpen] = useState(false);
 54→  const [isDesktop, setIsDesktop] = useState(() => {
 55→    if (typeof window === "undefined") {
 56→      return true;
 57→    }
 58→    return window.matchMedia("(min-width: 1024px)").matches;
 59→  });
 60→
 61→  const {
 62→    sessions,
 63→    archivedSessions,
 64→    selectedSessionId,
 65→    createSession,
 66→    deleteSession,
 67→    selectSession,
 68→    uploadSessionFile,
 69→    getSessionFile,
 70→    getSessionFileUrl,
 71→    listSessionDirectory,
 72→    refreshSession,
 73→    refreshSessions,
 74→    refreshArchivedSessions,
 75→    loadMoreSessions,
 76→    loadMoreArchivedSessions,
 77→    hasMoreSessions,
 78→    hasMoreArchivedSessions,
 79→    isLoadingMore,
 80→    isLoadingMoreArchived,
 81→    isLoadingArchived,
 82→    searchQuery,
 83→    setSearchQuery,
 84→    applySessionStatus,
 85→    fetchWorkDirs,
 86→    fetchStartupDir,
 87→    renameSession,
 88→    generateTitle,
 89→    archiveSession,
 90→    unarchiveSession,
 91→    bulkArchiveSessions,
 92→    bulkUnarchiveSessions,
 93→    bulkDeleteSessions,
 94→    forkSession,
 95→    error: sessionsError,
 96→  } = sessionsHook;
 97→
 98→  const currentSession = useMemo(
 99→    () => sessions.find((session) => session.sessionId === selectedSessionId),
100→    [sessions, selectedSessionId],
101→  );
102→
103→  const [streamStatus, setStreamStatus] = useState<ChatStatus>("ready");
104→
105→  useEffect(() => {
106→    const token = consumeAuthTokenFromUrl();
107→    if (token) {
108→      setAuthToken(token);
109→    }
110→  }, []);
111→
112→  // Create session dialog state (lifted to App for unified access)
113→  const [showCreateDialog, setShowCreateDialog] = useState(false);
114→
115→  const handleOpenCreateDialog = useCallback(() => {
116→    setShowCreateDialog(true);
117→    setIsMobileSidebarOpen(false);
118→  }, []);
119→
120→  const handleOpenMobileSidebar = useCallback(() => {
121→    setIsMobileSidebarOpen(true);
122→  }, []);
123→
124→  const handleCloseMobileSidebar = useCallback(() => {
125→    setIsMobileSidebarOpen(false);
126→  }, []);
127→
128→  // Sidebar state
129→  const [isSidebarCollapsed, setIsSidebarCollapsed] = useState(false);
130→  const [isSidebarAnimating, setIsSidebarAnimating] = useState(false);
131→  const handleCollapseSidebar = useCallback(() => {
132→    setIsSidebarAnimating(true);
133→    sidebarPanelRef.current?.collapse();
134→  }, []);
135→  const handleExpandSidebar = useCallback(() => {
136→    setIsSidebarAnimating(true);
137→    sidebarPanelRef.current?.expand();
138→  }, []);
139→  const handleSidebarResize = useCallback((panelSize: PanelSize) => {
140→    const collapsed = panelSize.inPixels <= SIDEBAR_COLLAPSED_SIZE + 1;
141→    setIsSidebarCollapsed((prev) => (prev === collapsed ? prev : collapsed));
142→  }, []);
143→
144→  useEffect(() => {
145→    if (!isSidebarAnimating) {
146→      return;
147→    }
148→    const timer = window.setTimeout(() => {
149→      setIsSidebarAnimating(false);
150→    }, SIDEBAR_ANIMATION_MS);
151→    return () => window.clearTimeout(timer);
152→  }, [isSidebarAnimating]);
153→
154→  useEffect(() => {
155→    const current = sidebarPanelRef.current;
156→    if (!current) {
157→      return;
158→    }
159→    setIsSidebarCollapsed(current.isCollapsed());
160→  }, []);
161→
162→  useEffect(() => {
163→    const element = sidebarElementRef.current;
164→    if (!element) {
165→      return;
166→    }
167→    if (isSidebarAnimating) {
168→      element.style.transition = `flex-basis ${SIDEBAR_ANIMATION_MS}ms ease-in-out`;
169→      return;
170→    }
171→    element.style.transition = "";
172→  }, [isSidebarAnimating]);
173→
174→  // Track layout breakpoint and close mobile sidebar when switching to desktop
175→  useEffect(() => {
176→    const mediaQuery = window.matchMedia("(min-width: 1024px)");
177→    const handleChange = () => {
178→      const matches = mediaQuery.matches;
179→      setIsDesktop(matches);
180→      if (matches) setIsMobileSidebarOpen(false);
181→    };
182→    handleChange();
183→    mediaQuery.addEventListener("change", handleChange);
184→    return () => mediaQuery.removeEventListener("change", handleChange);
185→  }, []);
186→
187→  // Track if we've restored session from URL
188→  const hasRestoredFromUrlRef = useRef(false);
189→
190→  // Eagerly restore session from URL - don't wait for session list to load
191→  // This allows session content to load in parallel with the session list
192→  useEffect(() => {
193→    if (hasRestoredFromUrlRef.current) {
194→      return;
195→    }
196→
197→    const urlSessionId = getSessionIdFromUrl();
198→    if (urlSessionId) {
199→      console.log("[App] Eagerly restoring session from URL:", urlSessionId);
200→      selectSession(urlSessionId);
201→    }
202→    hasRestoredFromUrlRef.current = true;
203→  }, [selectSession]);
204→
205→  // Validate session exists once session list loads, clear URL if not found
206→  useEffect(() => {
207→    if (sessions.length === 0 || !selectedSessionId) {
208→      return;
209→    }
210→
211→    if (searchQuery.trim() || hasMoreSessions) {
212→      return;
213→    }
214→
215→    const sessionExists = sessions.some(
216→      (s) => s.sessionId === selectedSessionId,
217→    );
218→    if (!sessionExists) {
219→      console.log("[App] Session from URL not found, clearing selection");
220→      updateUrlWithSession(null);
221→      selectSession("");
222→    }
223→  }, [sessions, selectedSessionId, selectSession, hasMoreSessions, searchQuery]);
224→
225→  // Update URL when selected session changes
226→  useEffect(() => {
227→    // Skip the initial render before URL restoration
228→    if (!hasRestoredFromUrlRef.current) {
229→      return;
230→    }
231→    updateUrlWithSession(selectedSessionId || null);
232→  }, [selectedSessionId]);
233→
234→  // Show toast notifications for errors
235→  useEffect(() => {
236→    if (sessionsError) {
237→      toast.error("Session Error", {
238→        description: sessionsError,
239→      });
240→    }
241→  }, [sessionsError]);
242→
243→  const handleStreamStatusChange = useCallback((nextStatus: ChatStatus) => {
244→    setStreamStatus(nextStatus);
245→  }, []);
246→
247→  const handleSessionStatus = useCallback(
248→    (status: SessionStatus) => {
249→      applySessionStatus(status);
250→
251→      if (status.state !== "idle") {
252→        return;
253→      }
254→
255→      const reason = status.reason ?? "";
256→      if (!reason.startsWith("prompt_")) {
257→        return;
258→      }
259→
260→      console.log(
261→        "[App] Prompt complete, refreshing session info:",
262→        status.sessionId,
263→      );
264→      refreshSession(status.sessionId);
265→    },
266→    [applySessionStatus, refreshSession],
267→  );
268→
269→  const handleCreateSession = useCallback(
270→    async (workDir: string, createDir?: boolean) => {
271→      await createSession(workDir, createDir);
272→    },
273→    [createSession],
274→  );
275→
276→  const handleCreateSessionInDir = useCallback(
277→    async (workDir: string) => {
278→      await createSession(workDir);
279→    },
280→    [createSession],
281→  );
282→
283→  const handleDeleteSession = useCallback(
284→    async (sessionId: string) => {
285→      await deleteSession(sessionId);
286→    },
287→    [deleteSession],
288→  );
289→
290→  const handleSelectSession = useCallback(
291→    (sessionId: string) => {
292→      selectSession(sessionId);
293→      setIsMobileSidebarOpen(false);
294→    },
295→    [selectSession],
296→  );
297→
298→  const handleRefreshSessions = useCallback(async () => {
299→    await refreshSessions();
300→  }, [refreshSessions]);
301→
302→  const handleSearchQueryChange = useCallback(
303→    (query: string) => {
304→      setSearchQuery(query);
305→    },
306→    [setSearchQuery],
307→  );
308→
309→  // Transform Session[] to SessionSummary[] for sidebar
310→  const sessionSummaries = useMemo(
311→    () =>
312→      sessions.map((session) => ({
313→        id: session.sessionId,
314→        title: session.title ?? "Untitled",
315→        updatedAt: formatRelativeTime(session.lastUpdated),
316→        workDir: session.workDir,
317→        lastUpdated: session.lastUpdated,
318→      })),
319→    [sessions],
320→  );
321→
322→  // Transform archived Session[] to SessionSummary[] for sidebar
323→  const archivedSessionSummaries = useMemo(
324→    () =>
325→      archivedSessions.map((session) => ({
326→        id: session.sessionId,
327→        title: session.title ?? "Untitled",
328→        updatedAt: formatRelativeTime(session.lastUpdated),
329→        workDir: session.workDir,
330→        lastUpdated: session.lastUpdated,
331→      })),
332→    [archivedSessions],
333→  );
334→
335→  const handleForkSession = useCallback(
336→    async (sessionId: string, turnIndex: number) => {
337→      await forkSession(sessionId, turnIndex);
338→    },
339→    [forkSession],
340→  );
341→
342→  const renderChatPanel = () => (
343→    <ChatWorkspaceContainer
344→      selectedSessionId={selectedSessionId}
345→      currentSession={currentSession}
346→      sessionDescription={currentSession?.title}
347→      onSessionStatus={handleSessionStatus}
348→      onStreamStatusChange={handleStreamStatusChange}
349→      uploadSessionFile={uploadSessionFile}
350→      onListSessionDirectory={listSessionDirectory}
351→      onGetSessionFileUrl={getSessionFileUrl}
352→      onGetSessionFile={getSessionFile}
353→      onOpenCreateDialog={handleOpenCreateDialog}
354→      onOpenSidebar={handleOpenMobileSidebar}
355→      generateTitle={generateTitle}
356→      onRenameSession={renameSession}
357→      onForkSession={handleForkSession}
358→    />
359→  );
360→
361→  return (
362→    <PromptInputProvider>
363→      <div className="box-border flex h-[100dvh] flex-col bg-background text-foreground px-[calc(0.75rem+var(--safe-left))] pr-[calc(0.75rem+var(--safe-right))] pt-[calc(0.75rem+var(--safe-top))] pb-1 lg:pb-[calc(0.75rem+var(--safe-bottom))] max-lg:h-[100svh] max-lg:overflow-hidden">
364→        <div className="mx-auto flex h-full min-h-0 w-full flex-1 flex-col gap-2 max-w-none">
365→          {isDesktop ? (
366→            <ResizablePanelGroup
367→              orientation="horizontal"
368→              className="min-h-0 flex-1 overflow-hidden"
369→            >
370→              {/* Sidebar */}
371→              <ResizablePanel
372→                id="sessions"
373→                collapsible
374→                collapsedSize={SIDEBAR_COLLAPSED_SIZE}
375→                defaultSize={SIDEBAR_DEFAULT_SIZE}
376→                minSize={SIDEBAR_MIN_SIZE}
377→                elementRef={sidebarElementRef}
378→                panelRef={sidebarPanelRef}
379→                onResize={handleSidebarResize}
380→                className={cn("relative min-h-0 border-r pl-0.5 pr-2 overflow-hidden")}
381→              >
382→                {/* Collapsed sidebar - vertical strip with logo and expand button */}
383→                <div
384→                  className={cn(
385→                    "absolute inset-0 flex h-full flex-col items-center py-3 transition-all duration-200 ease-in-out",
386→                    isSidebarCollapsed
387→                      ? "opacity-100 translate-x-0"
388→                      : "opacity-0 -translate-x-2 pointer-events-none select-none",
389→                  )}
390→                >
391→                  <a
392→                    href="https://www.kimi.com/code"
393→                    target="_blank"
394→                    rel="noopener noreferrer"
395→                    className="hover:opacity-80 transition-opacity"
396→                  >
397→                    <img
398→                      src="/logo.png"
399→                      alt="Kimi"
400→                      width={24}
401→                      height={24}
402→                      className="size-6"
403→                    />
404→                  </a>
405→                  <button
406→                    type="button"
407→                    aria-label="Expand sidebar"
408→                    className="mt-auto mb-1 inline-flex h-8 w-8 cursor-pointer items-center justify-center rounded-md text-muted-foreground transition-colors hover:bg-secondary/50 hover:text-foreground"
409→                    onClick={handleExpandSidebar}
410→                  >
411→                    <PanelLeftOpen className="size-4" />
412→                  </button>
413→                </div>
414→                {/* Expanded sidebar */}
415→                <div
416→                  className={cn(
417→                    "absolute inset-0 flex h-full min-h-0 flex-col gap-3 transition-all duration-200 ease-in-out",
418→                    isSidebarCollapsed
419→                      ? "opacity-0 translate-x-2 pointer-events-none select-none"
420→                      : "opacity-100 translate-x-0",
421→                  )}
422→                >
423→                  <SessionsSidebar
424→                    onDeleteSession={handleDeleteSession}
425→                    onSelectSession={handleSelectSession}
426→                    onRenameSession={renameSession}
427→                    onArchiveSession={archiveSession}
428→                    onUnarchiveSession={unarchiveSession}
429→                    onBulkArchiveSessions={bulkArchiveSessions}
430→                    onBulkUnarchiveSessions={bulkUnarchiveSessions}
431→                    onBulkDeleteSessions={bulkDeleteSessions}
432→                    onRefreshSessions={handleRefreshSessions}
433→                    onRefreshArchivedSessions={refreshArchivedSessions}
434→                    onLoadMoreSessions={loadMoreSessions}
435→                    onLoadMoreArchivedSessions={loadMoreArchivedSessions}
436→                    onOpenCreateDialog={handleOpenCreateDialog}
