 26→
 27→type ChatConversationProps = {
 28→  messages: LiveMessage[];
 29→  status: ChatStatus;
 30→  selectedSessionId?: string;
 31→  currentSession?: Session;
 32→  isReplayingHistory: boolean;
 33→  pendingApprovalMap: Record<string, boolean>;
 34→  onApprovalAction?: AssistantApprovalHandler;
 35→  canRespondToApproval: boolean;
 36→  blocksExpanded: boolean;
 37→  onCreateSession?: () => void;
 38→  isSearchOpen: boolean;
 39→  onSearchOpenChange: (open: boolean) => void;
 40→  onForkSession?: (turnIndex: number) => void;
 41→};
 42→
 43→export function ChatConversation({
 44→  messages,
 45→  status,
 46→  selectedSessionId,
 47→  isReplayingHistory,
 48→  pendingApprovalMap,
 49→  onApprovalAction,
 50→  canRespondToApproval,
 51→  blocksExpanded,
 52→  onCreateSession,
 53→  isSearchOpen,
 54→  onSearchOpenChange,
 55→  onForkSession,
 56→}: ChatConversationProps) {
 57→  const listRef = useRef<VirtualizedMessageListHandle>(null);
 58→  const [isAtBottom, setIsAtBottom] = useState(true);
 59→  const [highlightedIndex, setHighlightedIndex] = useState(-1);
 60→
 61→  // Handle Cmd+F / Ctrl+F
 62→  useEffect(() => {
 63→    const handleKeyDown = (e: KeyboardEvent) => {
 64→      if ((e.metaKey || e.ctrlKey) && e.key === "f") {
 65→        e.preventDefault();
 66→        onSearchOpenChange(true);
 67→      }
 68→    };
 69→
 70→    window.addEventListener("keydown", handleKeyDown);
 71→    return () => window.removeEventListener("keydown", handleKeyDown);
 72→  }, [onSearchOpenChange]);
 73→
 74→  const handleJumpToMessage = useCallback((messageIndex: number) => {
 75→    setHighlightedIndex(messageIndex);
 76→    listRef.current?.scrollToIndex(messageIndex);
 77→    // Clear highlight after a delay
 78→    setTimeout(() => setHighlightedIndex(-1), 2000);
 79→  }, []);
 80→
 81→  const handleScrollToBottom = useCallback(() => {
 82→    listRef.current?.scrollToBottom();
 83→  }, []);
 84→
 85→  const isLoadingResponse =
 86→    messages.length === 0 &&
 87→    (status === "streaming" || status === "submitted");
 88→  const isStartingEnvironment =
 89→    isLoadingResponse && status === "submitted" && !isReplayingHistory;
 90→
 91→  const hasSelectedSession = Boolean(selectedSessionId);
 92→  const emptyNoSessionState =
 93→    messages.length === 0 && !hasSelectedSession;
 94→  const emptySessionState =
 95→    messages.length === 0 &&
 96→    hasSelectedSession &&
 97→    !isLoadingResponse;
 98→
 99→  const hasMessages = messages.length > 0;
