 36→
 37→const HOME_DIR_REGEX = /^(\/Users\/[^/]+|\/home\/[^/]+)/;
 38→const TRAILING_SLASH_REGEX = /\/$/;
 39→
 40→type CreateSessionDialogProps = {
 41→  open: boolean;
 42→  onOpenChange: (open: boolean) => void;
 43→  onConfirm: (workDir: string, createDir?: boolean) => Promise<void>;
 44→  fetchWorkDirs: () => Promise<string[]>;
 45→  fetchStartupDir: () => Promise<string>;
