 47→
 48→/**
 49→ * Format a path for display:
 50→ * - Replace home directory with ~
 51→ * - For long paths, show ~/.../<last-two-segments>
 52→ */
 53→function formatPathForDisplay(path: string, maxSegments = 3): string {
 54→  const homeMatch = path.match(HOME_DIR_REGEX);
 55→  let displayPath = path;
 56→
 57→  if (homeMatch) {
 58→    displayPath = `~${path.slice(homeMatch[1].length)}`;
 59→  }
 60→
 61→  const segments = displayPath.split("/").filter(Boolean);
 62→
 63→  if (segments.length <= maxSegments) {
 64→    return displayPath.startsWith("~")
 65→      ? displayPath
 66→      : `/${segments.join("/")}`;
 67→  }
 68→
 69→  const prefix = displayPath.startsWith("~") ? "~" : "";
 70→  const lastSegments = segments.slice(-2).join("/");
 71→  return `${prefix}/.../${lastSegments}`;
 72→}
 73→
 74→// Module-level cache for work dirs (stale-while-revalidate)
 75→let cachedWorkDirs: string[] | null = null;
 76→
 77→export function CreateSessionDialog({
 78→  open,
 79→  onOpenChange,
 80→  onConfirm,
 81→  fetchWorkDirs,
 82→  fetchStartupDir,
 83→}: CreateSessionDialogProps): ReactElement {
 84→  const [workDirs, setWorkDirs] = useState<string[]>(
 85→    () => cachedWorkDirs ?? [],
 86→  );
 87→  const [inputValue, setInputValue] = useState("");
 88→  const [isLoading, setIsLoading] = useState(false);
 89→  const [isCreating, setIsCreating] = useState(false);
 90→  const [showConfirmCreate, setShowConfirmCreate] = useState(false);
 91→  const [pendingPath, setPendingPath] = useState("");
 92→  const [startupDir, setStartupDir] = useState("");
 93→  const [commandValue, setCommandValue] = useState("");
 94→  const isCreatingRef = useRef(false);
 95→  const commandListRef = useRef<HTMLDivElement>(null);
 96→
 97→  // Fetch startup dir and work dirs independently for progressive loading
 98→  useEffect(() => {
 99→    if (!open) {
100→      return;
101→    }
102→
103→    // Initialize from cache if available, still refresh in background
104→    if (cachedWorkDirs) {
105→      setWorkDirs(cachedWorkDirs);
106→    } else {
107→      setIsLoading(true);
108→    }
109→
110→    // Startup dir resolves fast — show it immediately and highlight it
111→    fetchStartupDir()
112→      .then((startup) => {
113→        if (startup) {
114→          setStartupDir(startup);
115→          setCommandValue(startup);
116→        }
117→      })
118→      .catch(() => {
119→        // Startup dir is optional for this dialog; ignore failures.
120→      });
121→
122→    // Work dirs may take longer — update cache when done
123→    fetchWorkDirs()
124→      .then((dirs) => {
125→        cachedWorkDirs = dirs;
126→        setWorkDirs(dirs);
127→      })
128→      .catch((error) => {
129→        console.error("Failed to fetch directories:", error);
130→      })
131→      .finally(() => {
132→        setIsLoading(false);
133→      });
134→  }, [open, fetchWorkDirs, fetchStartupDir]);
135→
136→  // Reset component state when dialog closes (cache persists at module level)
137→  useEffect(() => {
138→    if (!open) {
139→      setInputValue("");
140→      setCommandValue("");
141→      setWorkDirs(cachedWorkDirs ?? []);
142→      setIsCreating(false);
143→      setShowConfirmCreate(false);
144→      setPendingPath("");
145→      setStartupDir("");
146→      isCreatingRef.current = false;
147→    }
148→  }, [open]);
149→
150→  const handleSelect = useCallback(
151→    async (dir: string) => {
152→      if (isCreatingRef.current) return;
153→      isCreatingRef.current = true;
154→      setIsCreating(true);
155→      try {
156→        await onConfirm(dir);
157→        onOpenChange(false);
158→      } catch (err) {
159→        if (
160→          err instanceof Error &&
161→          "isDirectoryNotFound" in err &&
162→          (err as Error & { isDirectoryNotFound: boolean }).isDirectoryNotFound
163→        ) {
164→          setPendingPath(dir);
165→          setShowConfirmCreate(true);
166→        }
167→      } finally {
168→        setIsCreating(false);
169→        isCreatingRef.current = false;
170→      }
171→    },
172→    [onConfirm, onOpenChange],
173→  );
174→
175→  const handleInputSubmit = useCallback(() => {
176→    const trimmed = inputValue.trim();
177→    if (!trimmed || isCreatingRef.current) return;
178→    handleSelect(trimmed);
179→  }, [inputValue, handleSelect]);
180→
181→  const handleConfirmCreateDir = useCallback(async () => {
182→    if (!pendingPath) {
183→      return;
184→    }
185→
186→    setShowConfirmCreate(false);
187→    setIsCreating(true);
188→    isCreatingRef.current = true;
189→    try {
190→      await onConfirm(pendingPath, true);
191→      onOpenChange(false);
192→    } catch (err) {
193→      console.error("Failed to create directory:", err);
194→    } finally {
195→      setIsCreating(false);
196→      isCreatingRef.current = false;
197→      setPendingPath("");
198→    }
199→  }, [pendingPath, onConfirm, onOpenChange]);
200→
201→  const handleCancelCreateDir = useCallback(() => {
202→    setShowConfirmCreate(false);
203→    setPendingPath("");
204→  }, []);
205→
206→  // Tab completion: fill input with first matching item's value
207→  const handleKeyDown = useCallback(
208→    (e: React.KeyboardEvent) => {
209→      if (e.key !== "Tab" || !commandListRef.current) return;
210→
211→      // Find the currently selected (highlighted) item
212→      const selectedItem = commandListRef.current.querySelector<HTMLElement>(
213→        "[cmdk-item][data-selected=true]",
214→      );
215→      if (!selectedItem) return;
216→
217→      const value = selectedItem.getAttribute("data-value");
218→      if (!value || value.startsWith("__custom__")) return;
219→
220→      e.preventDefault();
221→      setInputValue(value);
222→    },
223→    [],
224→  );
225→
226→  // Check if the current input matches any existing work dir
227→  const trimmedInput = inputValue.trim();
228→  const inputMatchesExisting =
229→    trimmedInput !== "" &&
230→    workDirs.some(
231→      (dir) =>
232→        dir === trimmedInput ||
233→        dir === trimmedInput.replace(TRAILING_SLASH_REGEX, ""),
234→    );
235→
236→  const showCustomPathOption = trimmedInput !== "" && !inputMatchesExisting;
237→
238→  // Recent dirs = workDirs excluding startupDir
239→  const recentDirs = useMemo(
240→    () => (startupDir ? workDirs.filter((d) => d !== startupDir) : workDirs),
241→    [workDirs, startupDir],
242→  );
243→
244→  return (
245→    <>
246→      <CommandDialog
247→        open={open}
248→        onOpenChange={onOpenChange}
249→        title="Create New Session"
250→        description="Search directories or type a new path"
251→        showCloseButton={false}
252→      >
253→        <Command value={commandValue} onValueChange={setCommandValue}>
254→          <CommandInput
255→            placeholder="Search directories or type a path..."
256→            value={inputValue}
257→            onValueChange={setInputValue}
258→            onKeyDown={handleKeyDown}
259→          />
260→          <CommandList ref={commandListRef}>
261→            <CommandEmpty>
262→              {trimmedInput
263→                ? "No matching directories."
264→                : isLoading
265→                  ? "Loading directories..."
266→                  : "Type a path to start a new session."}
267→            </CommandEmpty>
268→
269→            {showCustomPathOption && (
270→              <>
271→                <CommandGroup heading="Custom Path">
272→                  <CommandItem
273→                    className="group"
274→                    value={`__custom__${trimmedInput}`}
275→                    onSelect={handleInputSubmit}
276→                    disabled={isCreating}
277→                  >
278→                    {isCreating ? (
279→                      <Loader2 className="animate-spin" />
280→                    ) : (
281→                      <FolderOpen />
282→                    )}
283→                    <span className="flex-1 truncate">{trimmedInput}</span>
284→                    <kbd className="pointer-events-none ml-auto hidden select-none rounded border bg-muted px-1.5 py-0.5 font-mono text-[10px] text-muted-foreground group-data-[selected=true]:inline-flex">
285→                      ↵
286→                    </kbd>
287→                  </CommandItem>
288→                </CommandGroup>
289→                {(startupDir || recentDirs.length > 0 || isLoading) && (
290→                  <CommandSeparator />
291→                )}
292→              </>
293→            )}
294→
295→            {startupDir && (
296→              <>
297→                <CommandGroup heading="Current Directory">
298→                  <CommandItem
299→                    className="group"
300→                    value={startupDir}
301→                    onSelect={() => handleSelect(startupDir)}
302→                    disabled={isCreating}
303→                  >
304→                    <Home />
305→                    <Tooltip>
306→                      <TooltipTrigger asChild>
307→                        <span className="truncate">
308→                          {formatPathForDisplay(startupDir, 3)}
309→                        </span>
310→                      </TooltipTrigger>
311→                      <TooltipContent side="right">
312→                        {startupDir}
313→                      </TooltipContent>
314→                    </Tooltip>
315→                    <kbd className="pointer-events-none ml-auto hidden select-none rounded border bg-muted px-1.5 py-0.5 font-mono text-[10px] text-muted-foreground group-data-[selected=true]:inline-flex">
316→                      ↵
317→                    </kbd>
318→                  </CommandItem>
319→                </CommandGroup>
320→                {(recentDirs.length > 0 || isLoading) && <CommandSeparator />}
321→              </>
322→            )}
323→
324→            {recentDirs.length > 0 && (
325→              <CommandGroup heading="Recent Directories">
326→                {recentDirs.map((dir) => (
327→                  <CommandItem
328→                    className="group"
329→                    key={dir}
330→                    value={dir}
331→                    onSelect={() => handleSelect(dir)}
332→                    disabled={isCreating}
333→                  >
334→                    <FolderOpen />
335→                    <Tooltip>
336→                      <TooltipTrigger asChild>
337→                        <span className="truncate">
338→                          {formatPathForDisplay(dir, 3)}
339→                        </span>
340→                      </TooltipTrigger>
341→                      <TooltipContent side="right">{dir}</TooltipContent>
342→                    </Tooltip>
343→                    <kbd className="pointer-events-none ml-auto hidden select-none rounded border bg-muted px-1.5 py-0.5 font-mono text-[10px] text-muted-foreground group-data-[selected=true]:inline-flex">
344→                      ↵
345→                    </kbd>
346→                  </CommandItem>
347→                ))}
348→              </CommandGroup>
349→            )}
350→
351→            {isLoading && (
352→              <div className="flex items-center justify-center py-4">
353→                <Loader2 className="size-4 animate-spin text-muted-foreground" />
354→              </div>
355→            )}
356→          </CommandList>
357→        </Command>
358→      </CommandDialog>
359→
360→      <AlertDialog open={showConfirmCreate} onOpenChange={setShowConfirmCreate}>
361→        <AlertDialogContent>
362→          <AlertDialogHeader>
363→            <AlertDialogTitle>Directory Not Found</AlertDialogTitle>
364→            <AlertDialogDescription>
365→              The directory{" "}
366→              <code className="bg-muted px-1 py-0.5 rounded text-foreground break-all">
367→                {pendingPath}
368→              </code>{" "}
369→              does not exist. Would you like to create it?
370→            </AlertDialogDescription>
371→          </AlertDialogHeader>
372→          <AlertDialogFooter>
373→            <AlertDialogCancel onClick={handleCancelCreateDir}>
374→              Cancel
375→            </AlertDialogCancel>
376→            <AlertDialogAction onClick={handleConfirmCreateDir}>
377→              Create Directory
378→            </AlertDialogAction>
379→          </AlertDialogFooter>
380→        </AlertDialogContent>
381→      </AlertDialog>
382→    </>
383→  );
384→}
