   1→import { useState, useCallback, useEffect, useRef } from "react";
   2→import type {
   3→  Session,
   4→  UploadSessionFileResponse,
   5→  SessionStatus,
   6→} from "../lib/api/models";
   7→import { SessionFromJSON } from "../lib/api/models/Session";
   8→import { apiClient } from "../lib/apiClient";
   9→import { getAuthHeader, getAuthToken } from "../lib/auth";
  10→import { formatRelativeTime, getApiBaseUrl } from "./utils";
  11→
  12→// Regex patterns for path normalization
  13→const LEADING_DOT_SLASH_REGEX = /^\.\/+/;
  14→const LEADING_SLASH_REGEX = /^\/+/;
  15→const TRAILING_WHITESPACE_REGEX = /\s+$/;
  16→
  17→export type SessionFileEntry = {
  18→  name: string;
  19→  type: "directory" | "file";
  20→  size?: number;
  21→};
  22→
  23→type UseSessionsReturn = {
  24→  /** List of sessions (API Session type) */
  25→  sessions: Session[];
  26→  /** List of archived sessions */
  27→  archivedSessions: Session[];
  28→  /** Currently selected session ID */
  29→  selectedSessionId: string;
  30→  /** Loading state */
  31→  isLoading: boolean;
  32→  /** Loading state for archived sessions */
  33→  isLoadingArchived: boolean;
  34→  /** Error message if any */
  35→  error: string | null;
  36→  /** Refresh sessions list from API */
  37→  refreshSessions: () => Promise<void>;
  38→  /** Refresh archived sessions list from API */
  39→  refreshArchivedSessions: () => Promise<void>;
  40→  /** Load more sessions for pagination */
  41→  loadMoreSessions: () => Promise<void>;
  42→  /** Load more archived sessions for pagination */
  43→  loadMoreArchivedSessions: () => Promise<void>;
  44→  /** Whether there are more sessions to load */
  45→  hasMoreSessions: boolean;
  46→  /** Whether there are more archived sessions to load */
  47→  hasMoreArchivedSessions: boolean;
  48→  /** Loading state for pagination */
  49→  isLoadingMore: boolean;
  50→  /** Loading state for archived pagination */
  51→  isLoadingMoreArchived: boolean;
  52→  /** Current search query */
  53→  searchQuery: string;
  54→  /** Update search query */
  55→  setSearchQuery: (query: string) => void;
  56→  /** Refresh a single session's data from API */
  57→  refreshSession: (sessionId: string) => Promise<Session | null>;
  58→  /** Create a new session */
  59→  createSession: (workDir?: string, createDir?: boolean) => Promise<Session>;
  60→  /** Delete a session by ID */
  61→  deleteSession: (sessionId: string) => Promise<boolean>;
  62→  /** Select a session */
  63→  selectSession: (sessionId: string) => void;
  64→  /** Apply a runtime session status update */
  65→  applySessionStatus: (status: SessionStatus) => void;
  66→  /** Get formatted relative time for a session */
  67→  getRelativeTime: (session: Session) => string;
  68→  /** Upload a file to a session's work_dir */
  69→  uploadSessionFile: (
  70→    sessionId: string,
  71→    file: File,
  72→  ) => Promise<UploadSessionFileResponse>;
  73→  /** List files in a session's work_dir path */
  74→  listSessionDirectory: (
  75→    sessionId: string,
  76→    path?: string,
  77→  ) => Promise<SessionFileEntry[]>;
  78→  /** Get a file from a session's work_dir */
  79→  getSessionFile: (sessionId: string, path: string) => Promise<Blob>;
  80→  /** Get the URL for a session file (for direct access/download) */
  81→  getSessionFileUrl: (sessionId: string, path: string) => string;
  82→  /** Fetch available work directories */
  83→  fetchWorkDirs: () => Promise<string[]>;
  84→  /** Fetch the startup directory */
  85→  fetchStartupDir: () => Promise<string>;
  86→  /** Rename a session */
  87→  renameSession: (sessionId: string, title: string) => Promise<boolean>;
  88→  /** Generate title using AI (backend reads messages from wire.jsonl) */
  89→  generateTitle: (sessionId: string) => Promise<string | null>;
  90→  /** Archive a session */
  91→  archiveSession: (sessionId: string) => Promise<boolean>;
  92→  /** Unarchive a session */
  93→  unarchiveSession: (sessionId: string) => Promise<boolean>;
  94→  /** Bulk archive sessions */
  95→  bulkArchiveSessions: (sessionIds: string[]) => Promise<number>;
  96→  /** Bulk unarchive sessions */
  97→  bulkUnarchiveSessions: (sessionIds: string[]) => Promise<number>;
  98→  /** Bulk delete sessions */
  99→  bulkDeleteSessions: (sessionIds: string[]) => Promise<number>;
 100→  /** Fork a session at a specific turn index */
 101→  forkSession: (sessionId: string, turnIndex: number) => Promise<Session>;
 102→};
 103→
 104→const normalizeSessionPath = (value?: string): string => {
 105→  if (!value) {
 106→    return ".";
 107→  }
 108→  const trimmed = value.trim();
 109→  if (trimmed === "" || trimmed === "/" || trimmed === ".") {
 110→    return ".";
 111→  }
 112→  const stripped = trimmed
 113→    .replace(LEADING_DOT_SLASH_REGEX, "")
 114→    .replace(LEADING_SLASH_REGEX, "")
 115→    .replace(TRAILING_WHITESPACE_REGEX, "");
 116→  return stripped === "" ? "." : stripped;
 117→};
 118→
 119→const PAGE_SIZE = 100;
 120→const AUTO_REFRESH_MS = 30_000;
 121→
 122→/**
 123→ * Custom error class for directory not found
 124→ */
 125→export class DirectoryNotFoundError extends Error {
 126→  isDirectoryNotFound = true;
 127→  constructor(message: string) {
 128→    super(message);
 129→    this.name = "DirectoryNotFoundError";
 130→  }
 131→}
 132→
 133→/**
 134→ * Hook for managing sessions with real API calls
 135→ */
 136→export function useSessions(): UseSessionsReturn {
 137→  // Sessions list (using API Session type)
 138→  const [sessions, setSessions] = useState<Session[]>([]);
 139→
 140→  // Archived sessions list
 141→  const [archivedSessions, setArchivedSessions] = useState<Session[]>([]);
 142→
 143→  // Currently selected session
 144→  const [selectedSessionId, setSelectedSessionId] = useState<string>("");
 145→
 146→  // Loading and error states
 147→  const [isLoading, setIsLoading] = useState(false);
 148→  const [isLoadingMore, setIsLoadingMore] = useState(false);
 149→  const [isLoadingArchived, setIsLoadingArchived] = useState(false);
 150→  const [isLoadingMoreArchived, setIsLoadingMoreArchived] = useState(false);
 151→  const [error, setError] = useState<string | null>(null);
 152→
 153→  const [hasMoreSessions, setHasMoreSessions] = useState(true);
 154→  const [hasMoreArchivedSessions, setHasMoreArchivedSessions] = useState(true);
 155→  const [searchQuery, setSearchQuery] = useState("");
 156→  const lastRefreshRef = useRef(0);
 157→
 158→  /**
 159→   * Refresh sessions list from API
 160→   */
 161→  const refreshSessions = useCallback(async () => {
 162→    setIsLoading(true);
 163→    setError(null);
 164→
 165→    try {
 166→      const sessionsList =
 167→        await apiClient.sessions.listSessionsApiSessionsGet({
 168→          limit: PAGE_SIZE,
 169→          offset: 0,
 170→          q: searchQuery.trim() || undefined,
 171→        });
 172→
 173→      // Update sessions list
 174→      setSessions(sessionsList);
 175→      setHasMoreSessions(sessionsList.length === PAGE_SIZE);
 176→      lastRefreshRef.current = Date.now();
 177→
 178→      // Don't auto-select first session - user can click on one or create a new one
 179→    } catch (err) {
 180→      const message =
 181→        err instanceof Error ? err.message : "Failed to load sessions";
 182→      setError(message);
 183→      console.error("Failed to refresh sessions:", err);
 184→    } finally {
 185→      setIsLoading(false);
 186→    }
 187→  }, [searchQuery]);
 188→
 189→  const loadMoreSessions = useCallback(async () => {
 190→    if (isLoadingMore || isLoading || !hasMoreSessions) {
 191→      return;
 192→    }
 193→    setIsLoadingMore(true);
 194→    setError(null);
 195→    try {
 196→      const offset = sessions.length;
 197→      const moreSessions =
 198→        await apiClient.sessions.listSessionsApiSessionsGet({
 199→          limit: PAGE_SIZE,
 200→          offset,
 201→          q: searchQuery.trim() || undefined,
 202→        });
 203→      setSessions((current) => [...current, ...moreSessions]);
 204→      setHasMoreSessions(moreSessions.length === PAGE_SIZE);
 205→      lastRefreshRef.current = Date.now();
 206→    } catch (err) {
 207→      const message =
 208→        err instanceof Error ? err.message : "Failed to load more sessions";
 209→      setError(message);
 210→      console.error("Failed to load more sessions:", err);
 211→    } finally {
 212→      setIsLoadingMore(false);
 213→    }
 214→  }, [hasMoreSessions, isLoading, isLoadingMore, searchQuery, sessions.length]);
 215→
 216→  const applySessionStatus = useCallback((status: SessionStatus) => {
 217→    setSessions((current) =>
 218→      current.map((session) =>
 219→        session.sessionId === status.sessionId
 220→          ? { ...session, status }
 221→          : session,
 222→      ),
 223→    );
 224→  }, []);
 225→
 226→  /**
 227→   * Refresh archived sessions list from API
 228→   */
 229→  const refreshArchivedSessions = useCallback(async () => {
 230→    setIsLoadingArchived(true);
 231→    try {
 232→      const basePath = getApiBaseUrl();
 233→      const response = await fetch(
 234→        `${basePath}/api/sessions/?archived=true&limit=${PAGE_SIZE}`,
 235→        {
 236→          headers: getAuthHeader(),
 237→        },
 238→      );
 239→      if (!response.ok) {
 240→        throw new Error("Failed to load archived sessions");
 241→      }
 242→      const data = await response.json();
 243→      // Convert snake_case to camelCase
 244→      const archivedList: Session[] = data.map(
 245→        (item: Record<string, unknown>) => ({
 246→          sessionId: item.session_id,
 247→          title: item.title,
 248→          lastUpdated: new Date(item.last_updated as string),
 249→          isRunning: item.is_running,
 250→          status: item.status,
 251→          workDir: item.work_dir,
 252→          sessionDir: item.session_dir,
 253→          archived: item.archived,
 254→        }),
 255→      );
 256→      setArchivedSessions(archivedList);
 257→      setHasMoreArchivedSessions(archivedList.length === PAGE_SIZE);
 258→    } catch (err) {
 259→      console.error("Failed to refresh archived sessions:", err);
 260→    } finally {
 261→      setIsLoadingArchived(false);
 262→    }
 263→  }, []);
 264→
 265→  /**
 266→   * Load more archived sessions for pagination
 267→   */
 268→  const loadMoreArchivedSessions = useCallback(async () => {
 269→    if (isLoadingMoreArchived || isLoadingArchived || !hasMoreArchivedSessions) {
 270→      return;
 271→    }
 272→    setIsLoadingMoreArchived(true);
 273→    try {
 274→      const basePath = getApiBaseUrl();
 275→      const offset = archivedSessions.length;
 276→      const response = await fetch(
 277→        `${basePath}/api/sessions/?archived=true&limit=${PAGE_SIZE}&offset=${offset}`,
 278→        {
 279→          headers: getAuthHeader(),
 280→        },
 281→      );
 282→      if (!response.ok) {
 283→        throw new Error("Failed to load more archived sessions");
 284→      }
 285→      const data = await response.json();
 286→      const moreArchived: Session[] = data.map(
 287→        (item: Record<string, unknown>) => ({
 288→          sessionId: item.session_id,
 289→          title: item.title,
 290→          lastUpdated: new Date(item.last_updated as string),
 291→          isRunning: item.is_running,
 292→          status: item.status,
 293→          workDir: item.work_dir,
 294→          sessionDir: item.session_dir,
 295→          archived: item.archived,
 296→        }),
 297→      );
 298→      setArchivedSessions((current) => [...current, ...moreArchived]);
 299→      setHasMoreArchivedSessions(moreArchived.length === PAGE_SIZE);
 300→    } catch (err) {
 301→      console.error("Failed to load more archived sessions:", err);
 302→    } finally {
 303→      setIsLoadingMoreArchived(false);
 304→    }
 305→  }, [
 306→    archivedSessions.length,
 307→    hasMoreArchivedSessions,
 308→    isLoadingArchived,
 309→    isLoadingMoreArchived,
 310→  ]);
 311→
 312→  // Refresh sessions list when search changes
 313→  useEffect(() => {
 314→    refreshSessions();
 315→  }, [refreshSessions]);
 316→
 317→  // Load archived sessions on initial mount (for showing the count)
 318→  useEffect(() => {
 319→    refreshArchivedSessions();
 320→  }, [refreshArchivedSessions]);
 321→
 322→  // Refresh when returning to the tab (throttled)
 323→  useEffect(() => {
 324→    const handleVisibilityChange = () => {
 325→      if (document.visibilityState !== "visible") {
 326→        return;
 327→      }
 328→      const now = Date.now();
 329→      if (now - lastRefreshRef.current < 60_000) {
 330→        return;
 331→      }
 332→      refreshSessions();
 333→    };
 334→    document.addEventListener("visibilitychange", handleVisibilityChange);
 335→    return () =>
 336→      document.removeEventListener("visibilitychange", handleVisibilityChange);
 337→  }, [refreshSessions]);
 338→
 339→  // Periodic refresh to catch sessions created outside the web UI
 340→  useEffect(() => {
 341→    if (searchQuery.trim()) {
 342→      return;
 343→    }
 344→    const interval = window.setInterval(() => {
 345→      if (document.visibilityState !== "visible") {
 346→        return;
 347→      }
 348→      if (isLoading || isLoadingMore) {
 349→        return;
 350→      }
 351→      refreshSessions();
 352→    }, AUTO_REFRESH_MS);
 353→    return () => window.clearInterval(interval);
 354→  }, [isLoading, isLoadingMore, refreshSessions, searchQuery]);
 355→
 356→  /**
 357→   * Refresh a single session's data from API
 358→   * Returns: Session (API type) or null if not found
 359→   * @param sessionId - The session ID to refresh
 360→   */
 361→  const refreshSession = useCallback(
 362→    async (sessionId: string): Promise<Session | null> => {
 363→      try {
 364→        const session =
 365→          await apiClient.sessions.getSessionApiSessionsSessionIdGet({
 366→            sessionId,
 367→          });
 368→
 369→        const isArchived = Boolean(session.archived);
 370→
 371→        if (isArchived) {
 372→          // Update archived list and ensure it doesn't appear in active list
 373→          setArchivedSessions((current) => {
 374→            const exists = current.some((s) => s.sessionId === sessionId);
 375→            if (!exists) {
 376→              return [session, ...current];
 377→            }
 378→            return current.map((s) =>
 379→              s.sessionId === sessionId ? session : s,
 380→            );
 381→          });
 382→          setSessions((current) =>
 383→            current.filter((s) => s.sessionId !== sessionId),
 384→          );
 385→        } else {
 386→          // Update active list and ensure it doesn't appear in archived list
 387→          setSessions((current) => {
 388→            const exists = current.some((s) => s.sessionId === sessionId);
 389→            if (!exists) {
 390→              return [session, ...current];
 391→            }
 392→            return current.map((s) =>
 393→              s.sessionId === sessionId ? session : s,
 394→            );
 395→          });
 396→          setArchivedSessions((current) =>
 397→            current.filter((s) => s.sessionId !== sessionId),
 398→          );
 399→        }
 400→
 401→        return session;
 402→      } catch (err) {
 403→        console.error("Failed to refresh session:", sessionId, err);
 404→        return null;
 405→      }
 406→    },
 407→    [],
 408→  );
 409→
 410→  /**
 411→   * Create a new session
 412→   * Returns: Session (API type)
 413→   * @param workDir - Optional working directory for the session
 414→   * @param createDir - Whether to auto-create directory if it doesn't exist
 415→   */
 416→  const createSession = useCallback(
 417→    async (workDir?: string, createDir?: boolean): Promise<Session> => {
 418→      setIsLoading(true);
 419→      setError(null);
 420→      try {
 421→        // Use fetch directly to support the work_dir parameter
 422→        const basePath = getApiBaseUrl();
 423→        const body: { work_dir?: string; create_dir?: boolean } = {};
 424→        if (workDir) {
 425→          body.work_dir = workDir;
 426→        }
 427→        if (createDir) {
 428→          body.create_dir = createDir;
 429→        }
 430→        const response = await fetch(`${basePath}/api/sessions/`, {
 431→          method: "POST",
 432→          headers: {
 433→            "Content-Type": "application/json",
 434→            ...getAuthHeader(),
 435→          },
 436→          body: Object.keys(body).length > 0 ? JSON.stringify(body) : undefined,
 437→        });
 438→
 439→        if (!response.ok) {
 440→          const data = await response.json();
 441→          // Check for 404 with "Directory does not exist" message
 442→          if (
 443→            response.status === 404 &&
 444→            typeof data.detail === "string" &&
 445→            data.detail.includes("Directory does not exist")
 446→          ) {
 447→            throw new DirectoryNotFoundError(data.detail);
 448→          }
 449→          throw new Error(data.detail || "Failed to create session");
 450→        }
 451→
 452→        const sessionData = await response.json();
 453→        const session = SessionFromJSON(sessionData);
 454→
 455→        // Update sessions list (add to beginning)
 456→        setSessions((current) => [session, ...current]);
 457→
 458→        // Select the new session
 459→        setSelectedSessionId(session.sessionId);
 460→
 461→        return session;
 462→      } catch (err) {
 463→        // Re-throw DirectoryNotFoundError without setting global error
 464→        // Use property check instead of instanceof for reliability
 465→        if (
 466→          err instanceof Error &&
 467→          "isDirectoryNotFound" in err &&
 468→          (err as DirectoryNotFoundError).isDirectoryNotFound
 469→        ) {
 470→          throw err;
 471→        }
 472→        const message =
 473→          err instanceof Error ? err.message : "Failed to create session";
 474→        setError(message);
 475→        throw err;
 476→      } finally {
 477→        setIsLoading(false);
 478→      }
 479→    },
 480→    [],
 481→  );
 482→
 483→  /**
 484→   * Delete a session
 485→   */
 486→  const deleteSession = useCallback(
 487→    async (sessionId: string): Promise<boolean> => {
 488→      setIsLoading(true);
 489→      setError(null);
 490→
 491→      try {
 492→        await apiClient.sessions.deleteSessionApiSessionsSessionIdDelete({
 493→          sessionId,
 494→        });
 495→
 496→        // Update sessions list
 497→        setSessions((current) => {
 498→          const next = current.filter((s) => s.sessionId !== sessionId);
 499→
 500→          // If we deleted the selected session, select the first remaining one
 501→          if (sessionId === selectedSessionId && next.length > 0) {
 502→            setSelectedSessionId(next[0].sessionId);
 503→          } else if (next.length === 0) {
 504→            setSelectedSessionId("");
 505→          }
 506→
 507→          return next;
 508→        });
 509→
 510→        return true;
 511→      } catch (err) {
 512→        const message =
 513→          err instanceof Error ? err.message : "Failed to delete session";
 514→        setError(message);
 515→        return false;
 516→      } finally {
 517→        setIsLoading(false);
 518→      }
 519→    },
 520→    [selectedSessionId],
 521→  );
