 668→      headers: getAuthHeader(),
 669→    });
 670→
 671→    if (!response.ok) {
 672→      throw new Error("Failed to fetch startup directory");
 673→    }
 674→
 675→    return response.json();
 676→  }, []);
 677→
 678→  /**
 679→   * Rename a session
 680→   */
 681→  const renameSession = useCallback(
