 683→      try {
 684→        const basePath = getApiBaseUrl();
 685→        const response = await fetch(
 686→          `${basePath}/api/sessions/${encodeURIComponent(sessionId)}`,
 687→          {
 688→            method: "PATCH",
 689→            headers: {
 690→              "Content-Type": "application/json",
 691→              ...getAuthHeader(),
 692→            },
 693→            body: JSON.stringify({ title }),
 694→          },
 695→        );
 696→
 697→        if (!response.ok) {
 698→          const data = await response.json();
