 749→
 750→  /**
 751→   * Archive a session
 752→   */
 753→  const archiveSession = useCallback(
 754→    async (sessionId: string): Promise<boolean> => {
 755→      try {
 756→        const basePath = getApiBaseUrl();
 757→        const response = await fetch(
 758→          `${basePath}/api/sessions/${encodeURIComponent(sessionId)}`,
 759→          {
 760→            method: "PATCH",
 761→            headers: {
 762→              "Content-Type": "application/json",
 763→              ...getAuthHeader(),
 764→            },
 765→            body: JSON.stringify({ archived: true }),
 766→          },
 767→        );
 768→
 769→        if (!response.ok) {
 770→          const data = await response.json();
 771→          throw new Error(data.detail || "Failed to archive session");
 772→        }
 773→
 774→        // Move session from active to archived list
 775→        setSessions((current) => {
 776→          const next = current.filter((s) => s.sessionId !== sessionId);
 777→          // If we archived the selected session, select another one
 778→          if (sessionId === selectedSessionId) {
 779→            if (next.length > 0) {
 780→              setSelectedSessionId(next[0].sessionId);
 781→            } else {
 782→              setSelectedSessionId("");
 783→            }
 784→          }
 785→          return next;
 786→        });
 787→
 788→        // Refresh archived sessions to get the updated list
 789→        await refreshArchivedSessions();
 790→
 791→        return true;
 792→      } catch (err) {
 793→        console.error("Failed to archive session:", err);
 794→        return false;
 795→      }
 796→    },
 797→    [refreshArchivedSessions, selectedSessionId],
 798→  );
 799→
 800→  /**
 801→   * Unarchive a session
 802→   */
 803→  const unarchiveSession = useCallback(
 804→    async (sessionId: string): Promise<boolean> => {
 805→      try {
 806→        const basePath = getApiBaseUrl();
 807→        const response = await fetch(
 808→          `${basePath}/api/sessions/${encodeURIComponent(sessionId)}`,
 809→          {
 810→            method: "PATCH",
 811→            headers: {
 812→              "Content-Type": "application/json",
 813→              ...getAuthHeader(),
 814→            },
 815→            body: JSON.stringify({ archived: false }),
 816→          },
 817→        );
 818→
 819→        if (!response.ok) {
 820→          const data = await response.json();
 821→          throw new Error(data.detail || "Failed to unarchive session");
 822→        }
 823→
 824→        // Remove from archived list
 825→        setArchivedSessions((current) =>
 826→          current.filter((s) => s.sessionId !== sessionId),
 827→        );
 828→
 829→        // Refresh active sessions to get the updated list
 830→        await refreshSessions();
 831→
 832→        return true;
 833→      } catch (err) {
 834→        console.error("Failed to unarchive session:", err);
 835→        return false;
 836→      }
 837→    },
