 839→  );
 840→
 841→  /**
 842→   * Bulk archive sessions
 843→   * Returns the number of successfully archived sessions
 844→   */
 845→  const bulkArchiveSessions = useCallback(
 846→    async (sessionIds: string[]): Promise<number> => {
 847→      const basePath = getApiBaseUrl();
